dict.string:
ObjC.import('stdlib')

// Helpers
function runCommand(...arguments) {
  const task = $.NSTask.alloc.init
  const stdout = $.NSPipe.pipe

  task.executableURL = $.NSURL.alloc.initFileURLWithPath(arguments[0])
  task.arguments = arguments.slice(1)
  task.standardOutput = stdout
  task.launchAndReturnError(false)

  const dataOut = stdout.fileHandleForReading.readDataToEndOfFile
  const stringOut = $.NSString.alloc.initWithDataEncoding(dataOut, $.NSUTF8StringEncoding).js

  return stringOut
}

// Set PATH with youtube-dl
const new_path = runCommand($.getenv('alfred_preferences') + '/workflows/' + $.getenv('alfred_workflow_uid') + '/_sharedresources', 'youtubedl')
$.setenv('PATH', new_path, 1)

// Main
const browser_name = $.getenv('browser')
const browser = Application(browser_name)
const url = $.getenv('url')
const close_browser = ($.getenv('close_tab') === 'true') ? true : false

const chromium_variants = ['Google Chrome', 'Chromium', 'Opera', 'Vivaldi', 'Brave Browser', 'Microsoft Edge']
const webkit_variants = ['Safari', 'Webkit']

if (close_browser && browser_name && $.system('youtube-dl --simulate --quiet ' + url) === 0) {
  if (chromium_variants.some(app_name => browser_name.startsWith(app_name))) {
    browser.windows().forEach(window => {
      window.tabs().forEach(tab => { if (tab.url() === url) tab.close() })
    })
  } else if (webkit_variants.some(app_name => browser_name.startsWith(app_name))) {
    browser.windows().forEach(window => {
      // Check in reverse order, otherwise closing tabs messes with the tab indexes
      window.tabs().reverse().forEach(tab => { if (tab.url() === url) tab.close() })
    })
  }
}

const frontmost_app_name = Application('System Events').applicationProcesses.where({ frontmost: true }).name()[0]
const frontmost_app = Application(frontmost_app_name)

const chromium_variants = ['Google Chrome', 'Chromium', 'Opera', 'Vivaldi', 'Brave Browser', 'Microsoft Edge']
const webkit_variants = ['Safari', 'Webkit']

let title, clipboard
let script_filter_items = []

// Get details from frontmost browser, if any
if (chromium_variants.some(app_name => frontmost_app_name.startsWith(app_name))) {
  title = frontmost_app.windows[0].activeTab.name()
  url = frontmost_app.windows[0].activeTab.url().trim()

  if (title.length === 0) title = 'Web page does not have a title'

  script_filter_items.push({ 'variables': { 'browser': frontmost_app_name }, 'title': title, 'subtitle': url, 'arg': url })
} else if (webkit_variants.some(app_name => frontmost_app_name.startsWith(app_name))) {
  title = frontmost_app.windows[0].currentTab.name()
  url = frontmost_app.windows[0].currentTab.url().trim()

  if (title.length === 0) title = 'Web page does not have a title'

  script_filter_items.push({ 'variables': { 'browser': frontmost_app_name }, 'title': title, 'subtitle': url, 'arg': url })
}

// Get details from clipboard
frontmost_app.includeStandardAdditions = true

clipboard = (() => {
  try {
    return frontmost_app.theClipboard()
  } catch(error) {
    console.log('Ignoring clipboard contents due to error ' + error.errorNumber + '.')
  }
})()

if (RegExp('^https?://').test(clipboard)) {
  title = 'Play clipboard URL'
  url = clipboard.trim()

  script_filter_items.push({ 'variables': { 'browser': false }, 'title': title, 'subtitle': url, 'arg': url })
}

if (script_filter_items.length === 0) {
  JSON.stringify({
    'items': [{
	  'title': 'No URL found',
      'subtitle': 'Did not find a URL in the clipboard or a supported browser as the frontmost app',
      'valid': false
    }]
  })
} else {
  JSON.stringify({ 'items': script_filter_items })
}

const frontmost_app_name = Application('System Events').applicationProcesses.where({ frontmost: true }).name()[0]
const frontmost_app = Application(frontmost_app_name)

const chromium_variants = ['Google Chrome', 'Chromium', 'Opera', 'Vivaldi', 'Brave Browser', 'Microsoft Edge']
const webkit_variants = ['Safari', 'Webkit']

// Get details from frontmost browser, if any
if (chromium_variants.some(app_name => frontmost_app_name.startsWith(app_name))) {
  url = frontmost_app.windows[0].activeTab.url().trim()
} else if (webkit_variants.some(app_name => frontmost_app_name.startsWith(app_name))) {
  url = frontmost_app.windows[0].currentTab.url().trim()
} else {
  throw new Error('You need a supported browser as your frontmost app')
}

JSON.stringify({ 'alfredworkflow': {
  'variables': { 'browser': `${frontmost_app_name}` },
  'arg': `${url}`
}})
