dict.string:
// Durstenfeld shuffle
function shuffle(string) {
  const array = string.split("")

  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }

  return array.join("")
}

function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function httpRequest(url) {
  const queryURL = $.NSURL.URLWithString(url)
  const requestData = $.NSData.dataWithContentsOfURL(queryURL);
  const requestString = $.NSString.alloc.initWithDataEncoding(requestData, $.NSUTF8StringEncoding).js

  if (requestString.legth === 0) throw "Unable to get page. Check your internet connection and the service: " + url
  return requestString
}

function run(argv) {
  const passwordLength = argv[0] || envVar("default_length") || 64
  const password = envVar("service") == "anuqrng" ?
    httpRequest("https://qrng.anu.edu.au/wp-content/plugins/colours-plugin/get_block_alpha.php") :
    httpRequest("https://www.random.org/strings/?num=20&len=20&digits=on&upperalpha=on&loweralpha=on&unique=off&format=plain&rnd=new").replaceAll("\n", "")

  return shuffle(password).slice(0, passwordLength)
}
